console.log(input);
input = input.split("");

let position = 0;

for (let i = 0; i < input.length; i++) {
    if(input[i] == "(") {
        position--;
    } else{
        position++;
    }
}

if (position < 0) {
    console.log(`The train is ${Math.abs(position)} stations to the west`);
}  else if(position > 0){
    console.log(`The train is ${position} stations to the east`);
} else {
    console.log(`It's in front of you`);
}

